package test1;

public class test1 {

    public static void main(String[] args) {
        // TODO Auto-generated method stub
        //三种移位方法
        //>>右移<<左移>>>不带符号位右移
        int x = 1;//x=00000000 00000000 00000000 00000001
        int y1 = x << 1;//y1=00000000 00000000 00000000 00000010
        int y2 = x >> 1;//y2=00000000 00000000 00000000 00000000
        int x1 = -1;//x1=00000000 00000000 00000000 11111111
        int y3 = x1 >> 1;//y3=00000000 00000000 00000000 11111111
        int y4 = x1 >>> 1;//y4=11111111 11111111 11111111 11111111
        int x2 = -2147483648;//x2=10000000 00000000 00000000 00000000
        int y5 = x2 >> 1;//y5=10000000 00000000 11111111 11111111
        int y6 = x2 << 1;//y6=00000000 00000000 00000000 00000000
        int x3 = 2147483647;//x3=11111111 11111111 11111111 11111111
        int y7 = x3 << 1;//y7=11111111 11111111 11111111 11111110
        System.out.println(y7);
        System.out.println(Math.sin(Math.PI));
    }

}
